<style lang="scss">
.AiPhoneFoot {
  display: flex;
  gap: 1%;
  text-align: center;
  z-index: -1;
  margin: 0 -8% 0 -6%;
  padding: 3% 6% 7%;
  border-radius: 0 0 70px 70px;
  background: rgba(217, 217, 217, 0.1);

  &__Link {
    display: flex;
    flex-direction: column;
    font-size: .7em;
    font-weight: 700;
    width: 100%;
    color: #5F8CA4;

    &-Icon {
      margin-bottom: 1%;
    }

    &-Text {
      margin-top: auto;
    }

    &.--current {
      color: #FFFFFF;

      path {
        fill: #009DFF;
      }
    }
  }
}
@media screen and (max-width: 1480px) {
  .AiPhoneFoot__Link {
    font-size: 0.5em;
  }
}
@media screen and (max-width: 1240px) {
  .AiPhoneFoot__Link {
    font-size: 0.4em;
    align-items: center;
  }
  .AiPhoneFoot__Link-Icon {
    width: 20px
  }
  .AiPhoneFoot__Link-Icon svg {
    width: 100%;
    height: 100%;
  }
}
@media screen and (max-width: 767px) {
  .AiPhoneFoot__Link {
    font-size: 0.2em;
  }
  .AiPhoneFoot__Link-Icon {
    width: 10px
  }

}
</style>